import Image from 'next/image'

interface ClsLabelInterface {
    src:string
}

const myLoader = ({ src, width, quality }) => {
    return `${src}`
}

function ClsLabel ({src}:ClsLabelInterface) {
    return (
        <Image
            loader={myLoader}
            src={src}
            alt="Cls Label"
            layout="fill"
            blurDataURL="data:image/jpeg;base64,"
            placeholder="blur"
        />
    )
}

export default ClsLabel;